                        <div style={{textAlign: 'center'}}>
                        <a>Don't have an account?</a>
                        <a style = {{marginLeft: '5px', color: '#2074BD'}} onClick = {()=>this.reset()}>Sign Up</a>
                    </div>}
                    </CardContent>
                </Card>
            </div>
      </div>

    );
  }
